def num_of_journalist #Va définir le nombre de journaliste

	puts "Combien il y a de journalistes ?"
	@journalists.length

end

#puts num_of_journalist


def journalist_with_number #on créé la méthode
	puts "Combien d'handle ont un numéro ?"

	count = 0 # le comptage qui débute à zéro

    @journalists.each do |journalist| #on prend le tableau qui va passer sur chacun des éléments qu'on appelle |journalist|

    i = 0 #pour l'instant on va inspecter le premier élément du tableau donc i = 0 et pas 1 parce que le 1er élément du tableau est à la position 0
        while i < journalist.length #donc tant que i est inférieur à la taille d'un élèment il inspecte tout l'élèment
            if ('0'..'9').include?(journalist[i]) #si il tombe sur un chiffre de 0 à 9 dans l'élèment
                count += 1 #il incrémente de 1 donc +1
                break #on break pour pas qu'il continue sur l'élèment et compter un autre chiffre de l'élèment
            end
            i += 1
        end
    end
    return count
end
#puts journalist_with_number


def number_of_aude #on créé la méthode
	puts "Combien de handle ont -aude- ?"

	count = 0  # le comptage qui débute à zéro

	@journalists.each do |journalist| #on prend le tableau qui va passer sur chacun des éléments qu'on appelle |journalist|

		i = 0 #on commence le comptage au premier nom

			if journalist.downcase.include?('aude') #si il tombe sur aude dans l'élèment
			count += 1 #il incrémente de 1 donc +1

			end
		end

return count
end
#puts number_of_aude

def journalist_first_upcase #on créé la méthode
  puts "Combien commencent par une majuscule ?"

  count = 0 # le compteur qui débute à zéro __ count = combien

  @journalists.each do |journalist| #On prend le tableau journalistes, et demande au programme de faire pour chaque élément |journalist|


  if ('A'..'Z').include?(journalist[1]) #si la deuxième lettre de l'élément journalist est entre A et Z
  count += 1 #alors incrémente de 1 donc +1 au count
  end
end
  count
end

#puts journalist_first_upcase

def  journalist_number_maj
	puts "Combien contiennent une majuscule ?"

	count = 0 #on compte à partir de 0 pour savoir combien.

	    @journalists.each do |journalist| #Dans le tableau journalists, on va demander de faire pour chaque élément |journalist|

    i = 0 #pour l'instant on va inspecter le premier élément du tableau donc i = 0 et pas 1 parce que le 1er élément du tableau est à la position 0
        while i < journalist.length #donc tant que i est inférieur à la taille d'un élèment il inspecte tout l'élèment
            if ('A'..'Z').include?(journalist[i]) #si il tombe sur une lettre entre A et Z dans l'élèment
                count += 1 #il incrémente de 1 donc +1
                break #on break pour pas qu'il continue sur l'élèment et compter un autre chiffre de l'élèment
            end
            i += 1
        end
    end
    return count
end
#puts journalist_number_maj

def  journalist_number_underscore
	puts "Combien de _underscore_ dans tous les pseudos confondus ?"

	count = 0 #on compte à partir de 0 pour savoir combien.

	    @journalists.each do |journalist| #Dans le tableau journalists, on va demander de faire pour chaque élément |journalist|

    i = 0 #pour l'instant on va inspecter le premier élément du tableau donc i = 0 et pas 1 parce que le 1er élément du tableau est à la position 0
        while i < journalist.length #donc tant que i est inférieur à la taille d'un élèment il inspecte tout l'élèment
            if ('_').include?(journalist[i])
                count += 1 #il incrémente de 1 donc +1

            end
            i += 1
        end
    end
    return count
end
