#Simple .length pour trouver la taille du tableau
puts "Combien y a t-il de journalistes dans cet array ?"
puts "Il y en a #{interview.length}"

#/\d/.match(handle)  va détecter si il y a un digit dans handle et renvoyé le premier
#trouvé ou rien du tout s'il ne trouve rien
#La valeur renvoyée n'est ni une string ni un integer, donc je transforme en string avec to_s
#Enfin je vérifie que match m'a renvoyé quelque chose de non vide avec empty?
def contientNb(handle)
  return !/\d/.match(handle).to_s.empty?
end

#Comme pour les digit, on détecte les majuscules avec le mot clé upper
def contientMaj(handle)
  return !/[[:upper:]]/.match(handle).to_s.empty?
end

#Ici on ne travaille que sur le 2e caractère du handle car le premier sera toujours @
#Et pareil, on test si c'est vide
def majStart(handle)
  return !/[[:upper:]]/.match(handle[1]).to_s.empty?
end





puts "\nCombien d'handle contient un numéro ?"
numero = 0
interview.each do |handle| #Ici on parcours le tableau interview
  if contientNb(handle)    #et on incrémente numéro si la méthode vue au dessus
    numero += 1            #renvoie true
  end
end

puts "Il y en a #{numero}"

puts "\nCombien de Aude ?"
aude = 0
interview.each do |handle| #Même chose mais cette fois il nous suffit de vérifier
  if handle =~ /Aude/      #la présence de Aude dans chaque handle et d'incrémenter
    aude+=1                #aude si c'est le cas
  end
end
puts "Il y en a #{aude}"

puts "\nCombien commencent par une majuscule ?"
nbMaj = 0
interview.each do |handle| #Même chose que pour numéro
  if majStart(handle)
    nbMaj +=1
  end
end
puts "Il y en a #{nbMaj}"

puts "\nCombien contiennent une majuscule ?"
nbMaj2 = 0
interview.each do |handle| #Toujours le même principe
  if contientMaj(handle)
    nbMaj2 += 1
  end
end
puts "Il y en a #{nbMaj2}"

puts "\nCombien de _ dans tous les pseudos confondus ?"
und = 0
interview.each do |handle|
  if handle =~ /_/        #Même principe que pour Aude mais avec le caractère _
    und+=1
  end
end
puts "Il y en a #{und}"


